import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function WeddingHallsbyid() {
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedPeriod, setSelectedPeriod] = useState("morning");
  const [numberOfPeople, setNumberOfPeople] = useState(200); // القيمة الافتراضية هي 200
  const [price, setPrice] = useState(0);

  const weddingHall = {
    name: "Royal Wedding Hall",
    address: "123 Main St, Cairo",
    governorate: "Cairo",
    region: "Zamalek",
    imageUrls: [
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
    ],
  };

  const calculatePrice = () => {
    const date = new Date(selectedDate);
    const month = date.getMonth();
    const dayOfWeek = date.getDay(); // 0 - Sunday, 1 - Monday, ..., 6 - Saturday
    let basePrice = 5000;

    // Price changes based on month
    if (month >= 3 && month <= 8) {
      basePrice += 1000;
    } else {
      basePrice += 500;
    }

    // Price changes based on day of the week
    if (dayOfWeek >= 0 && dayOfWeek <= 3) {
      basePrice += 500; // Sunday to Wednesday
    } else {
      basePrice += 1000; // Thursday to Saturday
    }

    // Adjust price based on the time of the day
    if (selectedPeriod === "morning") {
      basePrice -= 500; // Morning period (lower price)
    }

    // Adjust price based on the number of people
    const extraPeople = Math.max(0, numberOfPeople - 200); // Extra people above 200
    const extraCost = extraPeople * 1.5; // Additional cost per person
    basePrice += extraCost;

    // Set the calculated price
    setPrice(basePrice);
  };

  return (
    <div className="container mt-5">
      <div className="row">
        {/* Wedding Hall Details */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{weddingHall.name}</h5>
              <p className="card-text">
                <strong>Address:</strong> {weddingHall.address}
              </p>
              <p className="card-text">
                <strong>Governorate:</strong> {weddingHall.governorate}
              </p>
              <p className="card-text">
                <strong>Region:</strong> {weddingHall.region}
              </p>
            </div>
          </div>
        </div>

        {/* Wedding Hall Images */}
        <div className="col-md-6">
          <div className="row">
            {weddingHall.imageUrls.map((imageUrl, index) => (
              <div className="col-4" key={index}>
                <div className="card mb-3">
                  <img
                    src={imageUrl}
                    className="card-img-top"
                    alt={`Hall Image ${index + 1}`}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Booking Form Section */}
      {/* Booking Form Section */}
      <div className="row mt-5">
        <div className="col-md-12">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Wedding Booking</h5>
              <form>
                <div className="mb-3">
                  <label htmlFor="date" className="form-label">
                    Wedding Date
                  </label>
                  <input
                    type="date"
                    className="form-control"
                    id="date"
                    value={selectedDate}
                    onChange={(e) => setSelectedDate(e.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="period" className="form-label">
                    Select Period
                  </label>
                  <select
                    id="period"
                    className="form-control"
                    value={selectedPeriod}
                    onChange={(e) => setSelectedPeriod(e.target.value)}
                  >
                    <option value="morning">Morning</option>
                    <option value="evening">Evening</option>
                  </select>
                </div>
                <div className="mb-3">
                  <label htmlFor="people" className="form-label">
                    Number of People
                  </label>
                  <input
                    type="number"
                    className="form-control"
                    id="people"
                    value={numberOfPeople}
                    onChange={(e) => setNumberOfPeople(e.target.value)}
                    min="0"
                  />
                </div>
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={calculatePrice}
                >
                  Calculate Price
                </button>
              </form>

              {/* Display the calculated price */}
              {price > 0 && (
                <div className="mt-3">
                  <h6>
                    <strong>Total Price: </strong>
                    {price.toFixed(2)} EGP
                  </h6>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default WeddingHallsbyid;
